INSERT INTO `photo` VALUES (1,2,'https://picscore-s3.s3.ap-northeast-2.amazonaws.com/permanent/325c7f30-3d0c-4ee3-a3c7-460d4636d5b8.jpg',85,'{\"구도\": 85, \"노출\": 88, \"노이즈\": 80, \"선명도\": 90, \"화이트밸런스\": 87, \"다이나믹 레인지\": 82}','{\"구도\": \"인물 중심의 구도로 자연스럽지만 배경이 조금 어수선해 보임.\", \"노출\": \"전체적인 밝기가 적절하나 부드러운 디테일이 약간 부족함.\", \"노이즈\": \"전반적으로 괜찮지만 약간의 노이즈가 감지됨.\", \"선명도\": \"얼굴과 피사체가 선명하게 표현됨.\", \"화이트밸런스\": \"자연스러운 피부톤을 잘 유지하고 있음.\", \"다이나믹 레인지\": \"밝은 부분과 어두운 부분의 대비가 다소 부족함.\"}',1,'article','2025-04-02 16:08:08'),(4,1,'https://picscore-s3.s3.ap-northeast-2.amazonaws.com/permanent/d53043bd-25b3-4583-837e-6ed2d4219435.jpg',78,'{\"구도\": 75, \"노출\": 70, \"노이즈\": 85, \"선명도\": 80, \"화이트밸런스\": 80, \"다이나믹 레인지\": 75}','{\"구도\": \"전반적으로 안정적이나, 주 피사체가 명확하지 않음.  \", \"노출\": \"다소 과다 노출된 부분이 있으며, 하늘 영역은 밝음.  \", \"노이즈\": \"노이즈가 거의 없고 깨끗한 이미지.  \", \"선명도\": \"충분히 선명하지만, 일부 세부 사항이 흐림.  \", \"화이트밸런스\": \"자연스러운 색감이지만 약간의 색온도 조정 필요.  \", \"다이나믹 레인지\": \"전체적인 명암 대비가 아쉬움.\"}',0,'article','2025-04-02 16:10:02'),(6,2,'https://picscore-s3.s3.ap-northeast-2.amazonaws.com/permanent/c0779256-43c3-43ce-86e7-48b11490fbd0.jpg',87,'{\"구도\": 85, \"노출\": 80, \"노이즈\": 95, \"선명도\": 90, \"화이트밸런스\": 88, \"다이나믹 레인지\": 82}','{\"구도\": \"주제가 잘 중심에 위치해 있지만 배경 요소가 다소 분산돼 보입니다.\", \"노출\": \"밝기가 적절하지만, 약간의 하이라이트가 날아간 느낌이 있습니다.\", \"노이즈\": \"이미지 전반에 걸쳐 노이즈가 거의 없어서 깨끗하게 느껴집니다.\", \"선명도\": \"인물의 피부와 머리카락이 선명하게 표현되었습니다.\", \"화이트밸런스\": \"자연광을 잘 활용했지만 약간의 색온도 조정이 필요할 수 있습니다.\", \"다이나믹 레인지\": \"밝고 어두운 부분의 상세가 잘 표현되었지만 더 넓은 범위를 원할 수 있습니다.\"}',1,'article','2025-04-02 16:14:36'),(10,5,'https://picscore-s3.s3.ap-northeast-2.amazonaws.com/permanent/ed498025-8b16-46d5-b75a-7c3aa3e32b43.jpg',86,'{\"구도\": 85, \"노출\": 80, \"노이즈\": 95, \"선명도\": 90, \"화이트밸런스\": 85, \"다이나믹 레인지\": 80}','{\"구도\": \"잘 정돈된 경치로 깊이감이 느껴지는 구도입니다.\", \"노출\": \"일부 영역에서 과다 노출된 부분이 보입니다.\", \"노이즈\": \"노이즈가 거의 없는 깨끗한 이미지입니다.\", \"선명도\": \"전체적으로 선명하고 디테일이 잘 보입니다.\", \"화이트밸런스\": \"자연스러운 색감이지만 약간의 색온도 조정이 필요할 수 있습니다.\", \"다이나믹 레인지\": \"하이라이트와 쉐도우의 표현이 더 개선될 수 있습니다.\"}',1,'article','2025-04-02 17:06:27'),(11,7,'https://picscore-s3.s3.ap-northeast-2.amazonaws.com/permanent/f4c2c3f2-9361-4298-8aaa-3819efeca23d.jpg',88,'{\"구도\": 85, \"노출\": 95, \"노이즈\": 80, \"선명도\": 90, \"화이트밸런스\": 90, \"다이나믹 레인지\": 85}','{\"구도\": \"구도가 안정적이며, 주제가 중앙에 배치되어 시각적인 균형이 잘 잡혀 있습니다.  \", \"노출\": \"적절하게 노출되어 있으며, 하이라이트와 섀도우가 잘 조화되어 있습니다.  \", \"노이즈\": \"전반적으로 노이즈가 적지만, 일부 영역에서 약간의 질감이 느껴집니다.  \", \"선명도\": \"이미지가 선명하고 디테일이 잘 표현되어 있습니다.  \", \"화이트밸런스\": \"자연스러운 색온도가 잘 유지되어 있어 시원한 느낌을 줍니다.  \", \"다이나믹 레인지\": \"밝은 부분과 어두운 부분의 디테일이 잘 보존되어 있는 편입니다.\"}',1,'article','2025-04-02 17:11:13'),(15,10,'https://picscore-s3.s3.ap-northeast-2.amazonaws.com/permanent/450d6e76-a427-40d0-8e4c-9eaa9049774d.jpg',88,'{\"구도\": 90, \"노출\": 90, \"노이즈\": 95, \"선명도\": 85, \"화이트밸런스\": 88, \"다이나믹 레인지\": 80}','{\"구도\": \"로고들이 균형 있게 배치되어 있어 시각적으로 조화로움.  \", \"노출\": \"적절한 노출로 로고들이 잘 드러남.  \", \"노이즈\": \"노이즈가 거의 없는 깨끗한 이미지.  \", \"선명도\": \"전반적으로 선명하지만 일부 로고의 디테일이 부족할 수 있음.  \", \"화이트밸런스\": \"색상이 자연스럽지만 약간의 조정이 필요할 수 있음.  \", \"다이나믹 레인지\": \"명암 영역이 좀 더 다양하면 좋겠음.\"}',1,'article','2025-04-02 17:17:19'),(16,14,'https://picscore-s3.s3.ap-northeast-2.amazonaws.com/permanent/d1cf9fe2-0e4a-40c8-a400-c49cc2b5e10c.jpg',82,'{\"구도\": 80, \"노출\": 78, \"노이즈\": 90, \"선명도\": 85, \"화이트밸런스\": 82, \"다이나믹 레인지\": 75}','{\"구도\": \"음식이 잘 배치되어 있지만, 접시의 측면이 잘리지 않았으면 좋았을 것 같아요.\", \"노출\": \"약간 과다 노출된 부분이 있어 음식의 색상이 다소 왜곡된 듯합니다.\", \"노이즈\": \"이미지에 노이즈가 거의 없으며, 깔끔한 느낌입니다.\", \"선명도\": \"대체로 선명하지만, 일부 요소는 약간 흐릿하게 보입니다.\", \"화이트밸런스\": \"자연스러운 색감을 보이고 있지만, 약간의 따뜻한 톤이 있습니다.\", \"다이나믹 레인지\": \"밝음과 어두움의 차이가 약간 부족하여 전체적인 입체감이 아쉽습니다.\"}',1,'article','2025-04-02 17:18:15'),(24,14,'https://picscore-s3.s3.ap-northeast-2.amazonaws.com/permanent/6965f2f4-147f-4bff-84b1-89a015c0fc1c.jpg',86,'{\"구도\": 85, \"노출\": 80, \"노이즈\": 95, \"선명도\": 90, \"화이트밸런스\": 85, \"다이나믹 레인지\": 80}','{\"구도\": \"음식이 잘 배열되어 있어 시선이 집중된다.\", \"노출\": \"일부 부분의 밝기가 조금 과해 보인다.\", \"노이즈\": \"이미지 품질이 매우 깨끗하다.\", \"선명도\": \"재료들의 디테일이 잘 나타나 있다.\", \"화이트밸런스\": \"전체적